mapper.select:
SELECT DISTINCT qlname
from jjg_fbgc_qlgc_zdhpzd
where proname = #{proname}
  and htd = #{htd}

SELECT DISTINCT qlname from jjg_fbgc_qlgc_zdhpzd where proname = #{proname} and htd = #{htd}

    and userid in

        #{item}

SELECT COUNT(DISTINCT val)
from jjg_fbgc_qlgc_zdhpzd
where proname = #{proname}
  and htd = #{htd}
  and qlname = #{zx}

SELECT COUNT(DISTINCT val) from jjg_fbgc_qlgc_zdhpzd where proname = #{proname} and htd = #{htd} and
qlname=#{zx}

    and userid in

        #{item}

SELECT *, #{zx} as name
from jjg_fbgc_qlgc_zdhpzd
where proname = #{proname}
  and htd = #{htd}
  and cd LIKE '左幅%'
  and qlname = #{zx}
ORDER BY val, qdzh, FIELD(cd, ${result})

SELECT *, #{zx} as name from jjg_fbgc_qlgc_zdhpzd where proname = #{proname} and htd = #{htd} and cd LIKE '左幅%'
and qlname = #{zx}

    and userid in

        #{item}


ORDER BY val,qdzh,FIELD(cd, ${result})

SELECT *, #{zx} as name
from jjg_fbgc_qlgc_zdhpzd
where proname = #{proname}
  and htd = #{htd}
  and cd LIKE '右幅%'
  and qlname = #{zx}
ORDER BY val, qdzh, FIELD(cd, ${result})

SELECT *, #{zx} as name from jjg_fbgc_qlgc_zdhpzd where proname = #{proname} and htd = #{htd} and cd LIKE '右幅%'
and qlname = #{zx}

    and userid in

        #{item}


ORDER BY val,qdzh,FIELD(cd, ${result})
