mapper.select:
select  id,username,name,phone,post_id,
        dept_id,description,create_time,company_id,expire,type
from sys_user


        and (username like CONCAT('%',#{vo.keyword},'%')
        or  name like CONCAT('%',#{vo.keyword},'%')
        or  phone like CONCAT('%',#{vo.keyword},'%'))


        and create_time >= #{vo.createTimeBegin}


        and create_time <= #{vo.createTimeEnd}


        and company_id = #{vo.companyId}


        and dept_Id = #{vo.deptId}


order by id desc
