SELECT * FROM jjg_fbgc_sdgc_sdlqlmysd
WHERE proname = #{proname} and htd = #{htd} and fbgc= #{fbgc}
  and lqs not like '隧' and lqs not like '%连接线%' and left(zh,1) not like 'Z' and left(zh,1) not like 'Y' order by zh,FIELD(cw, '上面层','中面层','下面层')

