    and userid in

        #{item}

SELECT COUNT(DISTINCT val)
from jjg_fbgc_sdgc_zdhgzsd
where proname = #{proname}
  and htd = #{htd}
  and lxbs = #{zx}

SELECT COUNT(DISTINCT val) from jjg_fbgc_sdgc_zdhgzsd where proname = #{proname} and htd = #{htd} and lxbs=#{zx}

    and userid in

        #{item}

SELECT *
from jjg_fbgc_sdgc_zdhgzsd
where proname = #{proname}
  and htd = #{htd}
